DROP TABLE IF EXISTS `bang_tin`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `bang_tin` (
  `id_san_pham` int(10) NOT NULL AUTO_INCREMENT,
  `ten_san_pham` varchar(50) NOT NULL,
  `gia_san_pham` float NOT NULL,
  `hinh_san_pham` varchar(50) NOT NULL,
  `dia_chi` varchar(50) NOT NULL,
  `thong_tin_mo_ta` text NOT NULL,
  `ngay_dang` date NOT NULL,
  `luot_xem` int(11) NOT NULL,
  `so_dien_thoai` int(1) NOT NULL,
  `id_danh_muc` int(10) NOT NULL,
  `id_loai_dat` int(10) NOT NULL,
  `ten_dang_nhap` varchar(50) NOT NULL,
  `chieu_rong` float NOT NULL,
  `chieu_dai` float NOT NULL,
  `trang_thai` tinytext NOT NULL,
  PRIMARY KEY (`id_san_pham`),
  KEY `id_danh_muc` (`id_danh_muc`),
  KEY `ten_dang_nhap` (`ten_dang_nhap`),
  KEY `id_loai_dat` (`id_loai_dat`),
  CONSTRAINT `bang_tin_ibfk_1` FOREIGN KEY (`id_danh_muc`) REFERENCES `danh_muc` (`id_danh_muc`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `bang_tin_ibfk_2` FOREIGN KEY (`ten_dang_nhap`) REFERENCES `khach_hang` (`ten_dang_nhap`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=116 DEFAULT CHARSET=utf8mb4;
/*!40101 SET character_set_client = @saved_cs_client */;

